import React from 'react'
import { API_URL } from '../constants'


const initialState = {
    description: "",
    task_creator_id: "",
    classification_id: "",
    state: "",
    city: ""
  }


class CreateTaskForm extends React.Component {
    state = initialState

    componentDidMount() {
        this.setState({
            task_creator_id: this.props.currentUser.id
        })
    }


    handleInputChange = event => {
      const inputName = event.target.name
      this.setState({
        [inputName]: event.target.value
      })
    }


    handleSubmit = event => {
      event.preventDefault()

      fetch(A`/posts`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        credentials: "include",
        body: JSON.stringify(this.state)
      })
        .then(r => r.json())
        .then(newPost => {
          console.log(newPost)
          this.setState(initialState)
          this.props.renderNewPost(newPost)
          this.props.showPost(newPost.id)
        })
    }


    render () {

        let {title, body, board }= this.state


        return (
            <div className="post-form-container">
                <h4>Write a Post</h4>
                <form onSubmit={this.handleSubmit}>
                    <label>Title:</label>
                    <textarea name="title" onChange={this.handleInputChange} value={title} />
                    <label>Body:</label>
                     <textarea name="body" onChange={this.handleInputChange} value={body} />
                     <input type="submit" value="Submit" />
                </form>
             </div>
        )
    }

}

export default CreateTaskForm
