import React from 'react'
import { API_URL } from '../constants'


const initialState = {
    description: "",
    task_creator_id: "",
    classification_id: "",
    state: "",
    city: ""
  }


class CreateTaskForm extends React.Component {
    state = initialState

    componentDidMount() {
        this.setState({
            task_creator: this.props.currentUser
        })
    }


    handleInputChange = event => {
      const inputName = event.target.name
      this.setState({
        [inputName]: event.target.value
      })
    }


    handleSubmit = event => {
      event.preventDefault()

      fetch(API_URL + `/tasks`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        credentials: "include",
        body: JSON.stringify(this.state)
      })
        .then(r => r.json())
        .then(newTask => {
          this.setState(initialState)

        })
    }


    render () {

        let {description, task_creator_id, classification_id, state, city}= this.state
        let classifications = this.props.

        return (
            <div className="post-form-container">
                <h4>Write a Post</h4>
                <form onSubmit={this.handleSubmit}>
                    <label>Description:</label>
                    <textarea name="description" onChange={this.handleInputChange} value={description} />
                    <label>State:</label>
                     <textarea name="state" onChange={this.handleInputChange} value={state} />
                     <input type="submit" value="Submit" />
                     <label>City:</label>
                     <textarea name="city" onChange={this.handleInputChange} value={city} />
                     <input type="submit" value="Submit" />
                </form>
             </div>
        )
    }

}

export default CreateTaskForm
