import React from 'react'
import { API_URL } from '../constants'


const initialState = {
    description: "",
    classification: "",
    state: "",
    city: ""
  }


class CreateTaskForm extends React.Component {
    state = initialState




    handleInputChange = event => {
      const inputName = event.target.name
      this.setState({
        [inputName]: event.target.value
      })
    }


    handleSubmit = event => {
      event.preventDefault()

      fetch(API_URL + `/tasks`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        credentials: "include",
        body: JSON.stringify(this.state)
      })
        .then(r => r.json())
        .then(newTask => {
          this.setState(initialState)

        })
    }

    dropdownUpdate(array) {}

    render () {

        let {description, classification, state, city}= this.state
        let classifications = this.props.classifications
        let dropdownAction = classifications.map(classValue => <option value={classValue}>{classValue.name}</option>)
        return (
            <div className="form-container">
                <h4>Ask for Help</h4>
                <form onSubmit={this.handleSubmit}>
                    <label>Description:</label>
                    <textarea name="description" onChange={this.handleInputChange} value={description} rows="10" cols="40"/>
                    <label>State:</label>
                     <textarea name="state" onChange={this.handleInputChange} value={state} />
                     <label>City:</label>
                     <textarea name="city" onChange={this.handleInputChange} value={city} />
                     <label>Task Type:</label>
                      <select name="classification" onChange={this.handleInputChange} value={classification}>{classifications.map(classValue => <option value={classValue}>{classValue.name}</option>)}</select>
                      <input type="submit" value="Submit" />
                </form>
             </div>
        )
    }

}

export default CreateTaskForm
