import React from 'react'
import LendTask from './LendTask.js'
import { API_URL } from '../constants'


class LendTaskContainer extends React.Component {

    handleAcceptTask = (event, id, task) => {
        event.preventDefault()

        fetch(API_URL + `/tasks/`+ this.props.task.id, {
            method: "PATCH",
            headers: {
                "Content-Type": "application/json" 
            },
            credentials: "include",
            body: JSON.stringify(this.props.task)
        })
        .then(r => r.json())
        .then(updatedTask => {
            this.props.routerHistory.push("/profile")
        })
    }



    render() {

        let tasksArray = this.props.tasks

        let routerHistory = this.props.history

        let renderTasks = tasksArray.map(task => {
            return <LendTask 
                key={task.id}
                task={task}
                routerHistory = {routerHistory}

            />
        })



        return(
            <>

                <ul className= "lendtask">
                    {renderTasks}
                </ul>

            </>
        )
    }
}

export default LendTaskContainer;
