import React from 'react'
import LendTask from './LendTask.js'
import { API_URL } from '../constants'


class LendTaskContainer extends React.Component {

    state = {
        tasks: []
    }

    componentDidMount() {
        fetch(API_URL +"/tasks")
        .then(r => r.json())
        .then(tasksArray => {
            this.setState({
                tasks: tasksArray
            })
        })
    }

    handleAcceptTask = event => {
        event.preventDefault()

        fetch(API_URL + `/tasks`+ {}, {
            method: "PATCH",
            headers: {
                "Content-Type": "application/json" 
            },
            credentials: "include",
            body: JSON.stringify(this.ta)
        })
        .then(r => r.json())
        .then(updatedTask => {
            console.log("Task Updated")
        })
    }

    render() {
        return(
            <>
                <h1>
                    <div className="actions">
                        <button>Lend a Hand</button>
                        <button>Find a Friend</button>
                        <button>As For Help</button>
                    </div>
                </h1>
                <div>
                    {this.state.tasks.map(task => <LendTask key={task.id} task={task} handleAcceptTask= {this.handleAcceptTask}/>)}
                </div>


            </>
        )
    }
}

export default LendTaskContainer;
