import React from 'react'
import LendTask from './LendTask.js'
import { API_URL } from '../constants'


class LendTaskContainer extends React.Component {

    state = {
        tasks: []
    }

    componentDidMount() {
        this.getTasks()
    }

    getTasks() {
        fetch(API_URL +"/tasks")
        .then(r => r.json())
        .then(tasksArray => {
            this.setState({
                tasks: tasksArray
            })
        })
    }

    handleAcceptTask = event => {
        event.preventDefault()

        fetch(API_URL + `/tasks`+ {}, {
            method: "PATCH",
            headers: {
                "Content-Type": "application/json" 
            },
            credentials: "include",
            body: JSON.stringify(this.task)
        })
        .then(r => r.json())
        .then(updatedTask => {
            console.log("Task Updated")
        })
    }



    render() {

        let tasksArray = this.props.tasks

        let renderTasks = tasksArray.map(task => {
            return <LendTask 
                key={task.id}
                task={task}

            />
        })

        return(
            <>
                <h1>
                    <div className="actions">
                        <button>Lend a Hand</button>
                        <button>Find a Friend</button>
                        <button>As For Help</button>
                    </div>
                </h1>
                <ul className= "lendtask">
                    {renderTasks}
                </ul>

            </>
        )
    }
}

export default LendTaskContainer;
