import React from 'react'
import { Link } from 'react-router-dom'

class Header extends React.Component {

    state = {
        seachTerm: ""
    }

    handleLogout = () => {
        fetch("http://localhost:3000/logout", {
          method: "POST",
          credentials: "include"
        })
          .then(r => r.json())
          .then(() => {
            this.props.handleUpdateCurrentUser(null)
          })
      }

    render(){

        return (
          <>
            <header>
                <button>LeanOn.me</button>

                 <div className="actions">
                {this.props.currentUser ? (
            <>
                <Link to="/about_us">
                  <button>About Us</button>
                </Link>
                <button onClick={this.handleLogout}>Logout {this.props.currentUser.username}</button>
                <Link to="/profile">
                  <button>Profile Pic</button>
                </Link>
            </>
          ) : (
              <>
                <Link to="/about_us">
                  <button>About Us</button>
                </Link>
                <Link to="/login">
                  <button>Login</button>
                </Link>
              </>
            )}
        </div>
      </header>
      <h1>
        <div className="actions">
          <button>Lend a Hand</button>
          <button>Find a Friend</button>
          <button>As For Help</button>
        </div>
      </h1>
      </>
        )
    }
}

export default Header;
