import React from 'react'
import { API_URL } from '../constants'


class LendTask extends React.Component {

    handleAcceptTask = (event, id) => {
        event.preventDefault()

        fetch(API_URL + `/tasks/`+ this.props.task.id, {
            method: "PATCH",
            headers: {
                "Content-Type": "application/json" 
            },
            credentials: "include",
            body: JSON.stringify(this.props.task)
        })
        .then(r => r.json())
        .then(updatedTask => {
            this.props.routerHistory.push("/profile")
        })
    }

    render() {

        const {id, description, task_creator, task_doer, classification, state, city} = this.props.task
        return(

            <>
            {task_doer ? (
                null
            ) : (
                <li className= "lendtask">
                    {description}
                    <button onClick={this.handleAcceptTask}>Accept Request</button>
                </li>
            )}

            </>

        )
    }
}

export default LendTask;
